INSERT INTO public.animal_facts (id,"name",animal_breed_id) VALUES
	 ('41552cb6-ae78-4701-9e95-4a468e9213bb','Beagle to jedna z najstarszych ras psów myśliwskich – jej historia sięga starożytnej Grecji i Rzymu, gdzie wykorzystywano je do polowań na zwierzynę łowną.','2f17f1aa-cf0c-4aa8-8321-a70def6cb2f2'),
	 ('fa80855f-4b6f-4235-8c45-323807c7a1d9','Beagle słynie z wyjątkowego nosa. Mają jeden z najsilniejszych zmysłów węchu wśród psów i są często używane jako psy służbowe do wykrywania niebezpiecznych substancji, takich jak narkotyki i materiały wybuchowe.','2f17f1aa-cf0c-4aa8-8321-a70def6cb2f2'),
	 ('63d9aaa5-2770-438c-ab49-feacc8a0ea05','Beagle są bardzo towarzyskie i uwielbiają przebywać z ludźmi i innymi psami. Są również znani ze swojej energii i wesołego usposobienia, co czyni je idealnymi towarzyszami dla rodzin z dziećmi.','2f17f1aa-cf0c-4aa8-8321-a70def6cb2f2'),
	 ('a23c173f-8ace-4c08-a928-77550b60ee40','Buldog to łagodna i lojalna rasa, kiedyś hodowana do nęcenia byków, ale teraz jest popularnym zwierzakiem.','6ef10418-503e-4370-8704-4a6e62971a64'),
	 ('d81a751c-349b-44c5-8249-5ce681cf7a44','Rozpoznawalne po swojej krótkiej budowie i pomarszczonej twarzy, są przyjaznymi i wspaniałymi zwierzętami domowymi.','6ef10418-503e-4370-8704-4a6e62971a64'),
	 ('59d92387-71a8-45b9-a184-961a7a549618','Buldogi mogą się przegrzewać, więc zapewnij cień i wodę. Mają też problemy zdrowotne, takie jak trudności w oddychaniu, ale są kochane za swój wyjątkowy wygląd i kochające osobowości.','6ef10418-503e-4370-8704-4a6e62971a64'),
	 ('e672f84b-359b-4c02-9bea-d6ceef305ae0','Chihuahua to najmniejsza rasa psów, znana ze swojej wielkiej osobowości.','7ab9dde0-caaf-429a-8cc8-f16afd8d8753'),
	 ('d08c3f91-6996-471c-a761-6cf87d856f8b','Zostały pierwotnie wyhodowane w Meksyku i zostały nazwane na cześć meksykańskiego stanu Chihuahua.','7ab9dde0-caaf-429a-8cc8-f16afd8d8753'),
	 ('c172acdf-0ce9-4834-90d7-6d4f1ed3deed','Chihuahua są znane ze swojej lojalności i przywiązania do swoich właścicieli i są świetnymi psami na kolanach.','7ab9dde0-caaf-429a-8cc8-f16afd8d8753'),
	 ('643845da-5e1a-4348-b32e-655a7af7ff2e','Labrador Retriever to jedna z najpopularniejszych ras psów, znana ze swoich przyjaznych i towarzyskich osobowości.','415fd8cb-3746-471c-90ae-39cc66507e50');
INSERT INTO public.animal_facts (id,"name",animal_breed_id) VALUES
	 ('13332c37-5b68-40f6-a08a-87eefd0a212d','Pierwotnie były hodowane do polowania na zwierzynę łowną i są doskonałymi pływakami.','415fd8cb-3746-471c-90ae-39cc66507e50'),
	 ('d82b6253-bf70-4226-975a-4a2de9e942d1','Labradory są inteligentne, można je trenować i są świetnymi rodzinnymi zwierzętami domowymi i psami przewodnikami.','415fd8cb-3746-471c-90ae-39cc66507e50'),
	 ('7cebc7ac-6cdb-4819-822e-ffc7a878e071','Buldogi francuskie to mała, krępa rasa z unikalnymi „uszami nietoperza”.','42a4fbc7-cd20-48b9-a98b-90a2fa2d89d5'),
	 ('3b4f0cca-7fa8-44f3-87a0-b85d231ed854','Pierwotnie były hodowane w Anglii do nęcenia byków, ale teraz są znane ze swoich czułych i wyluzowanych osobowości.','42a4fbc7-cd20-48b9-a98b-90a2fa2d89d5'),
	 ('21e7323b-5928-4ef5-81ff-2a999cfc5d7f','Frenchies są popularną rasą towarzyszącą i są znane ze swojej lojalności i figlarnego charakteru.','42a4fbc7-cd20-48b9-a98b-90a2fa2d89d5'),
	 ('02425286-f328-4a50-95dd-6ac874b95b4a','Golden Retrievery są znane ze swoich przyjaznych i chętnych do pomocy osobowości.','7a326e6d-4b3f-4847-99e7-b6a92e297515'),
	 ('1ddc7984-4239-490e-84d0-31a3eb821472','Pierwotnie były hodowane w Szkocji do aportowania zwierzyny łownej i są doskonałymi pływakami.','7a326e6d-4b3f-4847-99e7-b6a92e297515'),
	 ('f2c5eaca-d847-493f-9758-a971998ed0c0','Goldeny są bardzo inteligentne i można je trenować, a także są świetnymi rodzinnymi zwierzętami domowymi, psami służbowymi oraz psami poszukiwawczymi i ratowniczymi.','7a326e6d-4b3f-4847-99e7-b6a92e297515'),
	 ('79e07a37-f339-49b1-9869-29038d54419b','Owczarki niemieckie są bardzo inteligentne i nadają się do szkolenia i często są używane jako psy policyjne i wojskowe.','81e3a409-7240-44e0-8342-a9a3307bf54e'),
	 ('688cf706-e08e-47dc-a751-3fefcd8dbb97','Pierwotnie były hodowane w Niemczech jako psy pasterskie i są znane ze swojego ochronnego charakteru.','81e3a409-7240-44e0-8342-a9a3307bf54e');
INSERT INTO public.animal_facts (id,"name",animal_breed_id) VALUES
	 ('44988b42-dba4-4ce1-a3f1-304052edc2d5','Owczarki niemieckie są wspaniałymi zwierzętami rodzinnymi, ale wymagają również dużo ćwiczeń i stymulacji umysłowej, aby były szczęśliwe i zdrowe.','81e3a409-7240-44e0-8342-a9a3307bf54e'),
	 ('fc5e87f5-2f87-46a2-aa4a-45c67a93ae58','Pudle są bardzo inteligentne i można je trenować oraz są znane ze swojego eleganckiego wyglądu.','e8f61e18-6b77-4305-976d-63d18ac37cf6'),
	 ('63dd5a71-e126-4f9f-ab4e-973ccf6474db','Pierwotnie były hodowane w Niemczech jako aportery wodne i są doskonałymi pływakami.','e8f61e18-6b77-4305-976d-63d18ac37cf6'),
	 ('087006dd-ef8f-4010-a8e1-a4f39f453e4f','Pudle są dostępne w trzech rozmiarach: standardowym, miniaturowym i zabawkowym i są znane ze swojej hipoalergicznej sierści i niskiego wypadania.','e8f61e18-6b77-4305-976d-63d18ac37cf6');
